class Prices extends React.Component {
    state = {
        currency: 'USD'
    }

    render () {
        let list = '';
        let currentCurrency = '';

        currentCurrency = () => {
            switch (this.state.currency) {
                case 'USD':
                    return this.props.bpi.USD;
                    break;
                case 'EUR':
                    return this.props.bpi.EUR;
                    break;
                case 'GBP':
                    return this.props.bpi.GBP;
                    break;
                default:
                    return this.props.bpi.USD;
                    break;
            }
        }



        return (
            <div>
                <ul className="list-group">
                    <li className="list-group-item">
                        Bitcoin rate for {currentCurrency.description} : <span className="badge badge-primary">{currentCurrency.code}</span> 
                        <strong>{currentCurrency.rate}</strong>
                    </li>
                </ul>
            </div>
        );
    }
}

export default Prices;
