import React, { Component } from "react";
import Layout from "../components/Layout";
import Perk from "../components/Perk";

import "../style/profile.scss";
import profileface from "../assets/profileface2.jpg";

export default class profile extends Component {
  constructor(props) {
    super(props);

    this.state = {
      editMode: true
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  render() {
    return (
      <Layout>
        <div className="profile-container card border-primary mb-3">
          <div className="card-header">Profile for ____USERNAME TODO_____</div>
          <div className="card-body">
            <img src={profileface} alt="Profilememes" />

            {!this.state.editMode ? (
              <div className="right">
                <h4 className="card-title">____Username Placeholder____</h4>
                <p className="card-text">____Password Placeholder____</p>
              </div>
            ) : (
              <div className="right">
                <form>
                  <input className="card-title" type="text" name="name" value="____Username Placeholder____">

                  </input>
                  <input className="card-text" type="text" name="name" value="____Password Placeholder____">

                  </input>
                </form>
              </div>
            )}
          </div>
        </div>
      </Layout>
    );
  }
}
