import React, { Component } from 'react'
import Layout from '../components/Layout';
import Perk from '../components/Perk';

import '../style/perktree.scss';

export default class perktree extends Component {

    constructor(props) {
        super(props);

        this.state = ({
            perkLevelList: []
        })
    }

    updatePerkLevelList(perkLevel) {
        console.log("here",perkLevel);
        this.state.perkLevelList.push( perkLevel)
    }

    checkPerkLevels() {
        console.log("perkLevelList", this.state.perkLevelList);
    }


    render() {
        return (
            <Layout>
                <div className="perktree-container">
                    <Perk updatePerkLevelList={() => this.updatePerkLevelList(perkLevel)} title={"Strength"} />
                    <Perk updatePerkLevelList={() => this.updatePerkLevelList()} title={"Perception"} />
                    <Perk updatePerkLevelList={() => this.updatePerkLevelList()} title={"Endurance"} />
                    <Perk updatePerkLevelList={() => this.updatePerkLevelList()} title={"Agility"} />
                    <Perk updatePerkLevelList={() => this.updatePerkLevelList()} title={"Charisma"} />
                    <Perk updatePerkLevelList={() => this.updatePerkLevelList()} title={"Intelligence"} />
                </div>    
                <button onClick={() => this.checkPerkLevels()}>CLICK MED</button>
            </Layout>
        )
    }
}
