import React, { Component } from "react";
import Layout from "../components/Layout";
import Perk from "../components/Perk";
import {IPerk} from "../interface";

import "../style/perktree.scss";

interface IProps {
}

interface IState {
    perkLevelList: Array<Function>,
    perkList: Array<String>
}

export default class perktree extends Component<IProps, IState> {
  constructor(props: IProps) {
    super(props);

    this.state = {
      perkLevelList: [],
      perkList: ["Strength", "Endurance", "Agility", "Charisma", "Intelligence"]
    };
  }

  checkPerkLevels() {
    console.log("perkLevelList", this.state.perkLevelList);
  }

  render() {
    return (
      <Layout>
        <div className="perktree-container">
          <Perk
            updatePerkLevelList={perkLevel =>
              this.updatePerkLevelList(perkLevel)
            }
            title={"Strength"}
          />
          <Perk
            updatePerkLevelList={perkLevel =>
              this.updatePerkLevelList(perkLevel)
            }
            title={"Endurance"}
          />
          <Perk
            updatePerkLevelList={perkLevel =>
              this.updatePerkLevelList(perkLevel)
            }
            title={"Agility"}
          />
          <Perk
            updatePerkLevelList={perkLevel =>
              this.updatePerkLevelList(perkLevel)
            }
            title={"Charisma"}
          />
          <Perk
            updatePerkLevelList={perkLevel =>
              this.updatePerkLevelList(perkLevel)
            }
            title={"Intelligence"}
          />
        </div>
        <button
          className="btn btn-success"
          onClick={() => this.checkPerkLevels()}
        >
          CLICK ME
        </button>
      </Layout>
    );
  }
}
