import React, { Component } from 'react'
import Perklevel from './Perklevel';

import image_placeholder from "../assets/404Code.jpg";
interface IProps {
    title?: string,
}

interface IState {
    title?: string,
    perkLevel: number,
}
let amountOfLevels: number = 5;

export default class Perk extends Component<IProps, IState> {
    constructor(props: IProps) {
        super(props);

        this.state = ({
            title: this.props.title,
            perkLevel: 0,
        })
    }

    createPerkLevels(amountOfLevels: number) {
        for (let index: number = 1; index < amountOfLevels; index++) {
            <Perklevel onClick={(e: Event) => this.PerklevelClicked(e, index)} image={image_placeholder}/>
        }
    }

    PerklevelClicked (e: Event, perkLevel: Component) {
        // if not previously assigned set opacity 1
        console.log("PerklevelClicked", this.state.title, perkLevel);
        e.target.style.opacity = '1';
        this.setState({
            perkLevel: perkLevel,
            oldElementTarget: e.target
        })
        // if  previously assigned set opacity .6 to old element and 1 to new element
        this.state.oldElementTarget ? this.state.oldElementTarget.style.opacity = '0.6' : null;

        //save perkLevel to state from each perk
        this.props.updatePerkLevelList({
            title: this.state.title, 
            perkLevel: perkLevel
        })
    }

    render() {
        return (
            <div className="perktree-perk-container">
                <ul className="perk">
                    <li className="perk-level title"><span>{this.props.title}</span></li>
                    {
                        this.createPerkLevels(amountOfLevels)
                    }
                </ul>
            </div>    
        )
    }
}
